import './App.css';
import arrow from './arrow.png';
import React, { useState, setState, useRef, useEffect } from 'react';

function App() {
	const [input, setInput] = useState('');
	const [styles, setStyles] = useState([
		{
			function_btn: 'function-btn',
			description: 'description',
			heading: 'heading',
		},
		{
			function_btn: 'function-btn',
			description: 'description',
			heading: 'heading',
		},
		{
			function_btn: 'function-btn',
			description: 'description',
			heading: 'heading',
		},
	]);

	function handleChange(event) {
		setInput(event.target.value);
	}
	function handleSubmit(event) {
		alert(input);
		event.preventDefault();
	}
	useEffect(() => {
		console.log(styles);
	}, [styles]);
	return (
		<div className="App">
			<div className="essay-header">
				<div className="title">Essay Dashboard</div>
			</div>
			<div className="box">
				<div className="text-input-output">
					<form>
						<textarea
							type="text"
							className="input"
							placeholder="Type something..."
							onChange={handleChange}
							align="left"
						></textarea>
					</form>
					<button onClick={handleSubmit}>
						<img src={arrow} className="arrow" alt="arrow"></img>
					</button>
					<form>
						<textarea
							type="text"
							className="output"
							placeholder="Response..."
						></textarea>
					</form>
				</div>
				<div className="function-sidebar">
					<div className="btn-holder">
						<SidebarButton
							index={0}
							heading="Rephrase sentences"
							description="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
								semper lobortis justo id laoreet."
						/>
						<button
							onClick={() => addStyles(0)}
							className={styles[0].function_btn}
						>
							<div className={styles[0].heading}>Rephrase sentences</div>
							<div className={styles[0].description}>
								Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
								semper lobortis justo id laoreet.
							</div>
						</button>
						<button
							onClick={() => addStyles(1)}
							className={styles[1].function_btn}
						>
							<div className={styles[1].heading}>Summarize sentences</div>
							<div className={styles[1].description}>
								Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
								semper lobortis justo id laoreet.
							</div>
						</button>
						<button
							onClick={() => addStyles(2)}
							className={styles[2].function_btn}
						>
							<div className={styles[2].heading}>Create an outline</div>
							<div className={styles[2].description}>
								Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
								semper lobortis justo id laoreet.
							</div>
						</button>
					</div>
				</div>
			</div>
		</div>
	);
}

function SidebarButton(props) {
	return (
		<button
			onClick={() => addStyles(props.index)}
			className={styles[props.index].function_btn}
		>
			<div className={styles[props.index].heading}>{props.heading}</div>
			<div className={styles[props.index].description}>{props.description}</div>
		</button>
	);
}

export default App;
