import './App.css';
import arrow from './arrow.png';
import React, { useState, setState, useRef, useEffect } from 'react';

function App() {
	const [input, setInput] = useState('');
	const [selected, setSelected] = useState({rephrase: false, summarize: false, outline: false, null: false});
	const [type, setType] = useState();

	function handleChange(event) {
		setInput(event.target.value);
	}
	function handleSubmit(event) {
		alert(input);
		event.preventDefault();
	}
	// useEffect(()=> {
	// 		if (!selected[type]) {
	// 		setSelected({ ...selected, type: true});
	// 	}
	// 	else {
	// 		setSelected({ ...selected, type: false});
	// 	}

	// });
	return (
		<div className="App">
			<div className="essay-header">
				<div className="title">
				Essay Dashboard
			</div>
			</div>
			<div className="box">
				<div className="text-input-output">
					<form>
						<textarea
							type="text"
							className="input"
							placeholder="Type something..."
							onChange={handleChange}
							align="left"
						></textarea>
					</form>
					<button onClick={handleSubmit}>
						<img src={arrow} className="arrow" alt="arrow"></img>
					</button>
					<form>
						<textarea
							type="text"
							className="output"
							placeholder="Response..."
						></textarea>
					</form>

				</div>
				<div className="function-sidebar">
					<div className="btn-holder">
					<button onClick={setType("rephrase")}>
						<div className="heading">Rephrase sentences</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					<button onClick={setType("rephrase")} className="function-btn">
						<div className="heading">Summarize sentences</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					<button onClick={setType("rephrase")} className="function-btn">
						<div className="heading">Create an outline</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
