import './App.css';
import arrow from './arrow.png';
import React, { useState, setState, useRef, useEffect } from 'react';

function App() {
	const [input, setInput] = useState('');
	const [styles, setStyles] = useState({"function-btn": "function-btn", "description": "description", "heading": "heading"});
	// const [style, setStyles] = useState("function-btn");

	function handleChange(event) {
		setInput(event.target.value);
	}
	function handleSubmit(event) {
		alert(input);
		event.preventDefault();
	}
	function addStyles(elem) {
		setStyles(styles => ({...styles, [elem]: styles[elem] + " active-" + elem}));
		setStyles(styles => ({...styles, "description": styles["description"] + " description"}));
		setStyles(styles => ({...styles, "heading": styles["heading"] + " heading"}));
		console.log(styles["border"]);
	}
	useEffect(() => {
		console.log(styles);
		// setStyles({...styles, border: "active-border"});
	})
	return (
		<div className="App">
			<div className="essay-header">
				<div className="title">
				Essay Dashboard
			</div>
			</div>
			<div className="box">
				<div className="text-input-output">
					<form>
						<textarea
							type="text"
							className="input"
							placeholder="Type something..."
							onChange={handleChange}
							align="left"
						></textarea>
					</form>
					<button onClick={handleSubmit}>
						<img src={arrow} className="arrow" alt="arrow"></img>
					</button>
					<form>
						<textarea
							type="text"
							className="output"
							placeholder="Response..."
						></textarea>
					</form>

				</div>
				<div className="function-sidebar">
					<div className="btn-holder">
					<button onClick={() => addStyles("border")} className={styles['function-btn']}>
						<div className="heading">Rephrase sentences</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					<button className="function-btn">
						<div className="heading">Summarize sentences</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					<button className="function-btn">
						<div className="heading">Create an outline</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
