import './App.css';
import arrow from './arrow.png';
import React, { useState, setState, useRef, useEffect } from 'react';

function App() {
	const [input, setInput] = useState('');
	const [closed, setClosed] = useState(false);
	const OCButton = useRef(null);
	let close = false;
	function handleChange(event) {
		setInput(event.target.value);
	}
	function handleSubmit(event) {
		alert(input);
		event.preventDefault();
	}
	function closeSidebar() {
		setClosed(true);
	}
	useEffect(()=> {
		console.log(closed);

	})
	return (
		<div className="App">
			<div className="essay-header">
				<div className="title">
				Essay Dashboard
			</div>
			</div>
			<div className="box">
				<div className="text-input-output">
					<form>
						<textarea
							type="text"
							className="input"
							placeholder="Type something..."
							onChange={handleChange}
							align="left"
						></textarea>
					</form>
					<button onClick={handleSubmit}>
						<img src={arrow} className="arrow" alt="arrow"></img>
					</button>
					<form>
						<textarea
							type="text"
							className="output"
							placeholder="Response..."
						></textarea>
					</form>

				</div>
				<div className="function-sidebar">
					<div className="open-close"><button className={close ? "closed-btn": "open-btn"} ref={OCButton} onClick={closeSidebar}>X</button></div>
					<div className="btn-holder">
					<button className="function-btn">
						<div className="heading">Rephrase sentences</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					<button className="function-btn">
						<div className="heading">Summarize sentences</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					<button className="function-btn">
						<div className="heading">Create an outline</div>
						<div className="description">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed semper lobortis justo id laoreet. </div>
					</button>
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
